import { Link } from "react-router-dom"


const Dropdown = ({ toggle, dropdown}) => {
    return (
        <div className={dropdown ? 'grid grid-rows-4 text-center bg-blue-400 ' : 'hidden'} onClick={toggle}>
            <Link className="p-4 hover:animate-pulse bg-gray-100" to='/'>Home</Link>
            <Link className="p-4 hover:animate-pulse hover:bg-gray-100" to='/menu'>Menu</Link>
            <Link className="p-4 hover:animate-pulse hover:bg-gray-100" to='/about'>About</Link>
            <Link className="p-4 hover:animate-pulse hover:bg-gray-100" to='/contact'>Contact</Link>
        </div>
    )
}

export default Dropdown
